import{_ as n,H as o,o as c,c as r,C as e,a as t,J as s}from"./chunks/framework.c4102992.js";const k=JSON.parse('{"title":"关于 ZQUMC","description":"","frontmatter":{"title":"关于 ZQUMC"},"headers":[],"relativePath":"about.md","filePath":"about.md"}'),i={name:"about.md"},l={id:"关于-zqumc",tabindex:"-1"},d=e("a",{class:"header-anchor",href:"#关于-zqumc","aria-label":'Permalink to "关于 ZQUMC <Badge type="warning" text="逐渐完善中" />"'},"​",-1),_=e("blockquote",null,[e("p",null,"砚园学子的 Minecraft 交流地")],-1),f=e("p",null,"砚园工坊（ZQUMC）成立于2023年5月， 是肇庆学院（Zhaoqing University）学生围绕游戏《Minecraft》建立的一个兴趣交流组织，成员来自肇庆学院的在读生及校友。",-1),h=e("p",null,"Minecraft（《我的世界》）是一款3D沙盒电子游戏。玩家可无拘无束地与由方块、实体构成的3个维度环境互动。多种玩法供玩家选择，带来无限可能。Minecraft 的玩家遍布全球，而 ZQUMC 致力于给校内 Minecraft 玩家提供一个游戏交流平台。",-1),M=e("p",null,"作为一个兴趣交流组织，ZQUMC 的主要活动包括 Minecraft 内容创作、举办 Minecraft 比赛、社群内部活动、举办校内外线上线下的交流和分享活动等。社群分享活动包括游戏技术分享、攻略讲解、开发讨论等，旨在提高社团成员的游戏技能及开发能力。线上交流和分享则是通过网络平台进行，为校内 Minecraft 玩家提供一个交流互动的空间，让大家可以分享游戏心得、交流游戏技巧。",-1),p=e("p",null,"除了活动之外，ZQUMC 还会为校内 Minecraft 玩家提供游戏服务器，以提供更好的游戏体验和服务。通过搭建服务器，玩家可以在一个共同的游戏环境中交流互动，共同完成内容创作，同时也可以享受到更好的游戏性能和更稳定的游戏体验。",-1),u=e("p",null,"ZQUMC 还致力于推广 Minecraft 游戏文化，以学校文化为核心，通过游戏建筑、游戏活动等形式，向校内外展示学校的历史文化和特色。同时，ZQUMC 也会与其他学校的 Minecraft 社团建立联系，进行跨校交流和合作，共同推广 Minecraft 游戏文化，促进校际间的交流和合作。",-1),m=e("h2",{id:"相关平台",tabindex:"-1"},[t("相关平台 "),e("a",{class:"header-anchor",href:"#相关平台","aria-label":'Permalink to "相关平台"'},"​")],-1);function C(U,Z,Q,b,x,g){const a=o("Badge");return c(),r("div",null,[e("h1",l,[t("关于 ZQUMC "),s(a,{type:"warning",text:"逐渐完善中"}),t(),d]),_,f,h,M,p,u,m])}const q=n(i,[["render",C]]);export{k as __pageData,q as default};
